function addHobbyClass(e,s){e.classList.add(classes[s%classes.length])}function hobbyEventListener(){hobbies.forEach(addHobbyClass)}function test(){$(function(){$(".info-left").fadeTo(500,1,linear),$(".info-right").fadeTo(500,1,linear)})}var hobbies=Array.from(document.getElementsByClassName("hobbies")),classes=["hobbies-slide-left","hobbies-slide-right"];hobbies.map(function(e){e.addEventListener("mouseover",hobbyEventListener),e.addEventListener("click",hobbyEventListener)});for(var i=0;i<5;i++)hobbies[i].addEventListener("transitionend",test,!1);