function hideHobbies(){switch(i){case 0:hobby0.style.display="none";break;case 1:hobby1.style.display="none";break;case 2:hobby2.style.display="none";break;case 3:hobby3.style.display="none";break;case 4:hobby4.style.display="none"}}for(var hobbies=document.querySelectorAll(".icons"),hobby0=document.getElementById("hiking"),hobby1=document.getElementById("cooking"),hobby2=document.getElementById("gaming"),hobby3=document.getElementById("engineering"),hobby4=document.getElementById("programming"),i=0;i<5;i++)hobbies[i].addEventListener("click",hideHobbies,!1);