function addHobbyClass(e,s){e.classList.add(classes[s%classes.length])}function hobbyEventListener(){hobbies.forEach(addHobbyClass)}function test(){$(function(){$(".info-down").animate({opacity:1})})}var hobbies=Array.from(document.getElementsByClassName("hobbies")),classes=["hobbies-slide-down"];hobbies.map(function(e){e.addEventListener("DOMContentLoaded",hobbyEventListener),e.addEventListener("click",hobbyEventListener)});for(var i=0;i<5;i++)hobbies[i].addEventListener("transitionend",test,!1);