function hideHobbies(){for(var e=0;e<5;e++)switch(e){case 0:hobbies[0].style.display="none";break;case 1:hobbies[1].style.display="none";break;case 2:hobbies[2].style.display="none";break;case 3:hobbies[3].style.display="none";break;case 4:hobbies[4].style.display="none"}}for(var hobbies=document.querySelectorAll(".icons"),i=0;i<5;i++)hobbies[i].addEventListener("click",hideHobbies,!1);